mapper.update:
UPDATE board_qna 
    SET answer_status = 1, recommend = #{recommend}, recommend_date = SYSDATE 
    WHERE board_id = #{board_id} AND qna_num = #{qna_num} AND seller_id = #{seller_id}

mapper.select:
SELECT a.qna_id,a.qna_num,a.board_id,a.seller_id,a.buyer_id, a.qna_status,a.answer_status,a.register_date,a.title,a.content,a.recommend,a.recommend_date, a.secret_flag, b.thumbnail_thum, b.thumbnail_thum_path,b.product_country, b.product_name, b.title AS board_title FROM( SELECT qna_id, qna_num, board_id, seller_id, buyer_id, qna_status, answer_status, register_date, title, content, recommend, recommend_date, secret_flag, row_number() OVER (ORDER BY recommend_date) AS rNum FROM board_qna
WHERE register_date >=to_date( #{startDate}, 'YYYY-MM-DD') AND register_date <= to_date(#{endDate}, 'YYYY-MM-DD')+1
) a, board_product b WHERE rNum between #{rowStart} AND #{rowEnd} AND buyer_id = #{id} AND a.board_id = b.board_id(+) ORDER BY recommend_date ASC
